import argparseimport sysfrom gensim.models.wrappers import FastTextimport osimport numpy as npdef main(*args):    parser = argparse.ArgumentParser()    parser.add_argument('--fasttext_model', required=True)    parser.add_argument('--dataset_file', required=True)    args = parser.parse_args(args=args)    ft = FastText()    model = ft.train(os.environ['FASTTEXT_PATH'], args.dataset_file, word_ngrams=3)    model.save(args.fasttext_model)    assert os.path.isfile(args.fasttext_model), 'file "{}" has not created!'.format(args.fasttext_model)    loaded_model = FastText.load(args.fasttext_model)    assert isinstance(loaded_model['счет'], np.ndarray)    print('Saved model to "{}"'.format(args.fasttext_model))if __name__ == '__main__':    main(*sys.argv[1:])